/* Reset and Base Styles */
* {
    margin: 0;
    padding: 0;
    box-sizing: border-box;
}

body {
    font-family: 'Inter', sans-serif;
    overflow-x: hidden;
    overflow-y: auto;
    background: linear-gradient(135deg, #ff4081 0%, #ff6b35 100%);
    min-height: 100vh;
    position: relative;
}

/* Navigation Styles */
.navbar {
    position: fixed;
    top: 0;
    left: 0;
    right: 0;
    z-index: 1000;
    background: rgba(0, 0, 0, 0.1);
    backdrop-filter: blur(10px);
    padding: 1rem 0;
    transition: all 0.3s ease;
}

.nav-container {
    max-width: 1200px;
    margin: 0 auto;
    display: flex;
    justify-content: space-between;
    align-items: center;
    padding: 0 2rem;
}

.brand-text {
    font-size: 1.5rem;
    font-weight: 900;
    color: white;
    text-transform: lowercase;
    background: linear-gradient(45deg, rgba(255, 255, 255, 0.9), rgba(255, 235, 59, 0.8));
    -webkit-background-clip: text;
    -webkit-text-fill-color: transparent;
    background-clip: text;
}

.nav-menu {
    display: flex;
    gap: 2rem;
}

.nav-link {
    color: white;
    text-decoration: none;
    font-weight: 500;
    font-size: 0.9rem;
    transition: all 0.3s ease;
    position: relative;
}

.nav-link:hover {
    color: #ffeb3b;
    transform: translateY(-2px);
}

.nav-link:after {
    content: '';
    position: absolute;
    bottom: -5px;
    left: 0;
    width: 0;
    height: 2px;
    background: #ffeb3b;
    transition: width 0.3s ease;
}

.nav-link:hover:after {
    width: 100%;
}

.nav-icons {
    display: flex;
    gap: 1rem;
}

.nav-icons i {
    color: white;
    font-size: 1.2rem;
    cursor: pointer;
    transition: all 0.3s ease;
}

.nav-icons i:hover {
    color: #ffeb3b;
    transform: scale(1.2);
}

/* Sections Container */
.sections-container {
    width: 100vw;
}

.section {
    width: 100vw;
    min-height: 100vh;
    position: relative;
    display: flex;
    align-items: center;
    justify-content: center;
    padding-top: 80px;
}

/* Section 1 - Hero */
.section-1 {
    background: linear-gradient(135deg, #ff4081 0%, #ff6b35 100%);
}

.hero-content {
    text-align: center;
    z-index: 10;
    position: relative;
}

.main-heading {
    font-size: clamp(3rem, 8vw, 8rem);
    font-weight: 900;
    color: white;
    line-height: 0.9;
    margin-bottom: 3rem;
    text-transform: lowercase;
}

.heading-line {
    display: block;
    animation: slideInUp 1s ease-out forwards;
    opacity: 0;
    transform: translateY(100px);
}

.heading-line:nth-child(1) {
    animation-delay: 0.2s;
}

.heading-line:nth-child(2) {
    animation-delay: 0.4s;
}

.heading-line:nth-child(3) {
    animation-delay: 0.6s;
}

/* CTA Button */
.cta-button {
    background: white;
    color: #ff4081;
    border: none;
    padding: 1rem 2.5rem;
    border-radius: 50px;
    font-size: 1.1rem;
    font-weight: 700;
    cursor: pointer;
    position: relative;
    overflow: hidden;
    transition: all 0.3s ease;
    animation: slideInUp 1s ease-out 0.8s forwards;
    opacity: 0;
    transform: translateY(100px);
}

.cta-button:hover {
    transform: translateY(-5px);
    box-shadow: 0 20px 40px rgba(0, 0, 0, 0.2);
}

.button-glow {
    position: absolute;
    top: 0;
    left: -100%;
    width: 100%;
    height: 100%;
    background: linear-gradient(90deg, transparent, rgba(255, 255, 255, 0.4), transparent);
    transition: left 0.5s ease;
}

.cta-button:hover .button-glow {
    left: 100%;
}

/* Download Buttons */
.download-buttons {
    display: flex;
    gap: 1rem;
    justify-content: center;
    animation: slideInUp 1s ease-out 0.8s forwards;
    opacity: 0;
    transform: translateY(100px);
}

.download-btn {
    color: white;
    border: none;
    padding: 0.8rem 1.5rem;
    border-radius: 12px;
    font-weight: 600;
    cursor: pointer;
    position: relative;
    overflow: hidden;
    transition: all 0.3s ease;
    display: flex;
    align-items: center;
    gap: 0.8rem;
    min-width: 180px;
    justify-content: flex-start;
    border: 1px solid rgba(255, 255, 255, 0.1);
}

.ios-btn {
    background: #000000;
    box-shadow: 0 8px 25px rgba(0, 0, 0, 0.3);
}

.ios-btn:hover {
    transform: translateY(-3px) scale(1.02);
    box-shadow: 0 12px 35px rgba(0, 0, 0, 0.4);
    background: #1a1a1a;
}

.android-btn {
    background: #000000;
    box-shadow: 0 8px 25px rgba(0, 0, 0, 0.3);
}

.android-btn:hover {
    transform: translateY(-3px) scale(1.02);
    box-shadow: 0 12px 35px rgba(0, 0, 0, 0.4);
    background: #1a1a1a;
}

.download-btn i {
    font-size: 1.8rem;
    flex-shrink: 0;
}

.btn-text {
    display: flex;
    flex-direction: column;
    align-items: flex-start;
    line-height: 1.2;
}

.btn-small {
    font-size: 0.75rem;
    opacity: 0.9;
    font-weight: 400;
    text-transform: uppercase;
    letter-spacing: 0.5px;
}

.btn-large {
    font-size: 1.1rem;
    font-weight: 600;
    margin-top: -2px;
}

@media (max-width: 768px) {
    .download-buttons {
        flex-direction: column;
        gap: 0.8rem;
    }

    .download-btn {
        width: 100%;
        max-width: 200px;
        margin: 0 auto;
    }
}

/* Floating Elements */
.floating-elements {
    position: absolute;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    pointer-events: none;
    z-index: 5;
}

.floating-element {
    position: absolute;
    animation: float 6s ease-in-out infinite;
}

.floating-element:nth-child(even) {
    animation-direction: reverse;
}

/* Hand Wave */
.hand-wave {
    top: 15%;
    left: 25%;
    font-size: 4rem;
    animation: wave 2s ease-in-out infinite;
}

.emoji {
    display: block;
    filter: drop-shadow(0 10px 20px rgba(0, 0, 0, 0.2));
}

/* Photo Frames */
.photo-frame {
    width: 140px;
    height: 180px;
    background: white;
    border-radius: 20px;
    box-shadow: 0 20px 40px rgba(0, 0, 0, 0.2);
    padding: 8px;
    overflow: hidden;
}

.frame-1 {
    top: 25%;
    left: 8%;
    transform: rotate(-10deg);
    animation-delay: -1s;
}

.frame-2 {
    top: 20%;
    right: 8%;
    transform: rotate(15deg);
    animation-delay: -3s;
}

.photo-img {
    width: 100%;
    height: 100%;
    object-fit: cover;
    border-radius: 15px;
}

/* Bubble Tea */
.bubble-tea {
    bottom: 15%;
    left: 8%;
    animation-delay: -2s;
}

.cup {
    width: 60px;
    height: 80px;
    background: linear-gradient(45deg, #ff6b35, #ff4081);
    border-radius: 0 0 30px 30px;
    position: relative;
    box-shadow: 0 10px 20px rgba(0, 0, 0, 0.2);
}

.straw {
    position: absolute;
    top: -20px;
    left: 50%;
    transform: translateX(-50%);
    width: 6px;
    height: 40px;
    background: #ffeb3b;
    border-radius: 3px;
}

.liquid {
    position: absolute;
    bottom: 5px;
    left: 5px;
    right: 5px;
    height: 50px;
    background: rgba(255, 255, 255, 0.3);
    border-radius: 0 0 25px 25px;
}

.bubbles {
    position: absolute;
    bottom: 10px;
    left: 50%;
    transform: translateX(-50%);
}

.bubbles span {
    display: inline-block;
    width: 8px;
    height: 8px;
    background: white;
    border-radius: 50%;
    margin: 0 1px;
    animation: bubble 2s ease-in-out infinite;
}

.bubbles span:nth-child(2) {
    animation-delay: 0.3s;
}

.bubbles span:nth-child(3) {
    animation-delay: 0.6s;
}

/* Balloon */
.balloon {
    top: 8%;
    right: 25%;
    animation-delay: -4s;
}

.balloon-body {
    width: 50px;
    height: 70px;
    background: #ffeb3b;
    border-radius: 50% 50% 50% 50% / 60% 60% 40% 40%;
    box-shadow: 0 10px 20px rgba(0, 0, 0, 0.2);
    position: relative;
}

.balloon-body:after {
    content: '';
    position: absolute;
    bottom: -10px;
    left: 50%;
    transform: translateX(-50%);
    width: 0;
    height: 0;
    border-left: 8px solid transparent;
    border-right: 8px solid transparent;
    border-top: 15px solid #ffeb3b;
}

.balloon-string {
    position: absolute;
    top: 70px;
    left: 50%;
    transform: translateX(-50%);
    width: 2px;
    height: 50px;
    background: rgba(0, 0, 0, 0.3);
}

/* Decorative Shapes */
.shape-circle {
    bottom: 20%;
    right: 12%;
    width: 40px;
    height: 40px;
    background: rgba(255, 255, 255, 0.3);
    border-radius: 50%;
    animation-delay: -5s;
}

.shape-star {
    bottom: 20%;
    right: 12%;
    color: #ffeb3b;
    font-size: 2rem;
    animation-delay: -1.5s;
}

/* Scroll Indicator */
.scroll-indicator {
    position: absolute;
    bottom: 2rem;
    left: 50%;
    transform: translateX(-50%);
    color: white;
    text-align: center;
    animation: bounce 2s infinite;
    cursor: pointer;
}

.scroll-indicator i {
    display: block;
    font-size: 1.5rem;
    margin-bottom: 0.5rem;
}

.scroll-indicator span {
    font-size: 0.9rem;
    font-weight: 500;
}

/* Section 2 - Roast & Dare Games */
.section-2 {
    background: #1a1a1a;
    color: white;
    position: relative;
    overflow: hidden;
}

.games-content {
    text-align: center;
    z-index: 10;
    position: relative;
    max-width: 800px;
    margin: 0 auto;
}

.games-heading {
    font-size: clamp(2.5rem, 6vw, 5rem);
    font-weight: 900;
    line-height: 1.1;
    margin-bottom: 3rem;
    text-transform: lowercase;
}

.heading-word {
    display: block;
    animation: slideInUp 1s ease-out forwards;
    opacity: 0;
    transform: translateY(100px);
}

.heading-word:nth-child(1) {
    animation-delay: 0.2s;
}

.heading-word:nth-child(2) {
    animation-delay: 0.4s;
}

.heading-word:nth-child(3) {
    animation-delay: 0.6s;
}

.highlight {
    background: linear-gradient(45deg, #ff4081, #ff6b35);
    -webkit-background-clip: text;
    -webkit-text-fill-color: transparent;
    background-clip: text;
}

.game-options {
    display: flex;
    flex-direction: column;
    gap: 1rem;
    align-items: center;
}

.option-line {
    background: rgba(255, 255, 255, 0.1);
    backdrop-filter: blur(10px);
    padding: 1rem 2rem;
    border-radius: 30px;
    font-size: 1.1rem;
    font-weight: 500;
    border: 1px solid rgba(255, 255, 255, 0.2);
    transition: all 0.3s ease;
    cursor: pointer;
    animation: slideInUp 1s ease-out forwards;
    opacity: 0;
    transform: translateY(50px);
}

.option-line:nth-child(1) { animation-delay: 0.8s; }
.option-line:nth-child(2) { animation-delay: 1.0s; }
.option-line:nth-child(3) { animation-delay: 1.2s; }
.option-line:nth-child(4) { animation-delay: 1.4s; }
.option-line:nth-child(5) { animation-delay: 1.6s; }
.option-line:nth-child(6) { animation-delay: 1.8s; }

.option-line:hover {
    background: rgba(255, 64, 129, 0.2);
    border-color: #ff4081;
    transform: translateY(-5px) scale(1.02);
    box-shadow: 0 10px 25px rgba(255, 64, 129, 0.2);
}

/* Chat Bubbles */
.chat-bubbles {
    position: absolute;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    pointer-events: none;
    z-index: 5;
}

.chat-bubble {
    position: absolute;
    background: white;
    color: #1a1a1a;
    padding: 0.8rem 1.2rem;
    border-radius: 20px;
    font-size: 0.9rem;
    font-weight: 500;
    box-shadow: 0 10px 25px rgba(0, 0, 0, 0.2);
    max-width: 200px;
    transition: all 0.3s ease;
    cursor: pointer;
}

.chat-bubble:hover {
    transform: scale(1.05);
    box-shadow: 0 15px 35px rgba(0, 0, 0, 0.3);
}

.chat-bubble:after {
    content: '';
    position: absolute;
    bottom: -8px;
    left: 20px;
    width: 0;
    height: 0;
    border-left: 10px solid transparent;
    border-right: 10px solid transparent;
    border-top: 10px solid white;
}

/* Circular arrangement around the center */
.bubble-1 {
    top: 10%;
    left: 50%;
    transform: translateX(-50%);
}

.bubble-2 {
    top: 25%;
    right: 15%;
}

.bubble-3 {
    top: 50%;
    right: 10%;
    transform: translateY(-50%);
}

.bubble-4 {
    bottom: 25%;
    right: 15%;
}

.bubble-5 {
    bottom: 10%;
    left: 50%;
    transform: translateX(-50%);
}

.bubble-6 {
    bottom: 25%;
    left: 15%;
}

.bubble-7 {
    top: 50%;
    left: 10%;
    transform: translateY(-50%);
}

/* Section 2 Floating Elements */
.section2-floating {
    position: absolute;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    pointer-events: none;
    z-index: 3;
}

