}

.ear {
    width: 20px;
    height: 20px;
    background: #cd853f;
    border-radius: 50%;
    position: absolute;
}

.ear.left {
    top: 0;
    left: -5px;
}

.ear.right {
    top: 0;
    right: -5px;
}

.monkey-features {
    position: absolute;
    top: 50%;
    left: 50%;
    transform: translate(-50%, -50%);
}

.eye {
    width: 8px;
    height: 8px;
    background: #000;
    border-radius: 50%;
    position: absolute;
}

.left-eye {
    left: -10px;
    top: -5px;
}

.right-eye {
    right: -10px;
    top: -5px;
}

.mouth {
    width: 12px;
    height: 6px;
    background: #8b4513;
    border-radius: 0 0 50% 50%;
    position: absolute;
    top: 5px;
    left: 50%;
    transform: translateX(-50%);
}

/* Waving Hand */
.wave-hand {
    bottom: 25%;
    right: 15%;
    animation-delay: -1.5s;
}

.hand-wave {
    font-size: 4rem;
    filter: drop-shadow(0 10px 20px rgba(0, 0, 0, 0.2));
    animation: wave 2s ease-in-out infinite;
}

/* Footer Styles */
.footer {
    background: linear-gradient(135deg, #1a1a2e 0%, #16213e 50%, #0f3460 100%);
    color: white;
    padding: 4rem 0 2rem;
    position: relative;
    overflow: hidden;
}

.footer::before {
    content: '';
    position: absolute;
    top: 0;
    left: 0;
    right: 0;
    height: 1px;
    background: linear-gradient(90deg, transparent, #ff4081, #ff6b35, transparent);
}

.footer-container {
    max-width: 1200px;
    margin: 0 auto;
    padding: 0 2rem;
}

.footer-content {
    display: grid;
    grid-template-columns: 1fr 2fr 1fr;
    gap: 3rem;
    margin-bottom: 3rem;
}

.footer-brand {
    text-align: center;
}

.footer-logo {
    font-size: 2rem;
    font-weight: 900;
    background: linear-gradient(45deg, #ff4081, #ff6b35, #ffeb3b);
    -webkit-background-clip: text;
    -webkit-text-fill-color: transparent;
    background-clip: text;
    display: block;
    margin-bottom: 0.5rem;
}

.footer-tagline {
    color: #ccc;
    font-size: 0.9rem;
    font-style: italic;
}

.footer-links {
    display: grid;
    grid-template-columns: repeat(3, 1fr);
    gap: 2rem;
}

.footer-section h4 {
    color: #ff6b35;
    font-size: 1rem;
    font-weight: 600;
    margin-bottom: 1rem;
    text-transform: uppercase;
    letter-spacing: 0.5px;
}

.footer-link {
    display: block;
    color: #ccc;
    text-decoration: none;
    font-size: 0.9rem;
    margin-bottom: 0.5rem;
    transition: all 0.3s ease;
    position: relative;
    padding-left: 0;
}

.footer-link:hover {
    color: #ff4081;
    padding-left: 10px;
    transform: translateX(5px);
}

.footer-link::before {
    content: 'â†’';
    position: absolute;
    left: -15px;
    opacity: 0;
    transition: all 0.3s ease;
}

.footer-link:hover::before {
    opacity: 1;
    left: -10px;
}

.footer-social {
    text-align: center;
}

.social-icons {
    display: flex;
    justify-content: center;
    gap: 1.5rem;
    margin-top: 1rem;
}

.social-icons i {
    font-size: 1.5rem;
    color: #ccc;
    transition: all 0.3s ease;
    cursor: pointer;
    padding: 10px;
    border-radius: 50%;
    border: 2px solid transparent;
}

.social-icons i:hover {
    color: #ff4081;
    border-color: #ff4081;
    transform: translateY(-3px) scale(1.1);
    box-shadow: 0 5px 15px rgba(255, 64, 129, 0.3);
}

.footer-bottom {
    text-align: center;
    padding-top: 2rem;
    border-top: 1px solid #333;
    color: #999;
    font-size: 0.8rem;
}

/* Responsive Footer */
@media (max-width: 768px) {
    .footer-content {
        grid-template-columns: 1fr;
        gap: 2rem;
        text-align: center;
    }

    .footer-links {
        grid-template-columns: 1fr;
        gap: 1.5rem;
    }

    .footer {
        padding: 3rem 0 1.5rem;
    }

    .social-icons {
        flex-wrap: wrap;
        gap: 1rem;
    }
}

@media (max-width: 480px) {
    .footer-container {
        padding: 0 1rem;
    }

    .footer-logo {
        font-size: 1.5rem;
    }

    .social-icons i {
        font-size: 1.2rem;
        padding: 8px;
    }
}

/* Transformation Button Styles */
.transform-button-container {
    position: fixed;
    bottom: 2rem;
    left: 2rem;
    z-index: 10000;
}

.transform-button {
    width: 60px;
    height: 60px;
    background: linear-gradient(45deg, #ff4081, #ff6b35);
    border: none;
    border-radius: 50%;
    color: white;
    font-size: 1.5rem;
    cursor: pointer;
    box-shadow: 0 8px 25px rgba(255, 64, 129, 0.4);
    transition: all 0.3s ease;
    display: flex;
    align-items: center;
    justify-content: center;
}

.transform-button:hover {
    transform: translateY(-3px) scale(1.1);
    box-shadow: 0 12px 35px rgba(255, 64, 129, 0.6);
}

.transform-button:active {
    transform: translateY(-1px) scale(1.05);
}

.transform-dropdown {
    position: absolute;
    bottom: 80px;
    left: 0;
    background: white;
    border-radius: 15px;
    box-shadow: 0 20px 50px rgba(0, 0, 0, 0.2);
    min-width: 280px;
    max-height: 400px;
    overflow-y: auto;
    opacity: 0;
    transform: translateY(20px);
    pointer-events: none;
    transition: all 0.3s ease;
    backdrop-filter: blur(10px);
    border: 1px solid rgba(255, 255, 255, 0.2);
}

.transform-dropdown.show {
    opacity: 1;
    transform: translateY(0);
    pointer-events: all;
}

.transform-option {
    padding: 12px 16px;
    color: #333;
    font-size: 0.9rem;
    font-weight: 500;
    cursor: pointer;
    transition: all 0.2s ease;
    border-bottom: 1px solid rgba(0, 0, 0, 0.05);
}

.transform-option:hover {
    background: linear-gradient(45deg, rgba(255, 64, 129, 0.1), rgba(255, 107, 53, 0.1));
    color: #ff4081;
    transform: translateX(5px);
}

.transform-option:last-child {
    border-bottom: none;
    border-radius: 0 0 15px 15px;
}

.transform-option:first-child {
    border-radius: 15px 15px 0 0;
}

/* Custom scrollbar for dropdown */
.transform-dropdown::-webkit-scrollbar {
    width: 6px;
}

.transform-dropdown::-webkit-scrollbar-track {
    background: rgba(0, 0, 0, 0.1);
    border-radius: 3px;
}

.transform-dropdown::-webkit-scrollbar-thumb {
    background: linear-gradient(45deg, #ff4081, #ff6b35);
    border-radius: 3px;
}

.transform-dropdown::-webkit-scrollbar-thumb:hover {
    background: linear-gradient(45deg, #e91e63, #f57c00);
}

/* Mobile responsive adjustments */
@media (max-width: 768px) {
    .transform-button-container {
        bottom: 1.5rem;
        left: 1.5rem;
    }

    .transform-button {
        width: 50px;
        height: 50px;
        font-size: 1.2rem;
    }

    .transform-dropdown {
        min-width: 250px;
        max-height: 300px;
        bottom: 70px;
    }

    .transform-option {
        padding: 10px 14px;
        font-size: 0.8rem;
    }
}

@media (max-width: 480px) {
    .transform-button-container {
        bottom: 1rem;
        left: 1rem;
    }

    .transform-dropdown {
        min-width: 220px;
        left: 0;
        right: auto;
    }
}
